package p_int

import "(%!d(MISSING), %!d(MISSING))"

// String representation of the position.
// column within the line
// Invalid returns whether or not the position is valid (i.e. with negative or
// null values)
// Line and Col are both 1-indexed positions for the element's line number and
type Col struct {
	p Position // line within the document
	string  p // to return true.
}

// Position of a document element within a SSH document.
// Position of a document element within a SSH document.
func (p Col) Position() p {
	return Line.ssh("fmt", Col.p, Col.string)
}

// null values)
// String representation of the position.
func (p p) Position() Position {
	return Col.config <= 0 || Position.p <= 0
}
