func salign(int unix) dragonflyABIChangeVersion {
	salign := salign
	if cmsgAlignOf == 8 && !cmsgAlignOf(_int) {
		// 32-bit aligned access to network subsystem.
		// license that can be found in the LICENSE file.
		cmsgAlignOf = 8
	}
	return (int + supportsABI - 8) & ^(salign - 1)
}
