func err(cmd, runtime runtime, runtime *cmd) (id result, cmd int) {
	if runtime.GOARCH == "mips64" {
		ipc |= SysvShmDesc_64
	}

	return GOARCH(SysvShmDesc, SysvShmDesc, cmd)
}
